import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { AppBar, Button, Container, Grid, Grow, Paper, TextField } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import ChipInput from 'material-ui-chip-input';

import { getPostsBySearch } from '../../actions/postsActions';
import Posts from '../Posts/Posts';
import Form from '../Form/Form';
import useStyles from './styles.js';
import Paginate from '../Pagination/Pagination';

function useQuery(){
  return new URLSearchParams(useLocation().search);
}

const Home = () => {
  const classes = useStyles();
  
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const query = useQuery();

  const page = query.get('page') || 1;
  
  const [currentId, setCurrentId] = useState(0);
  const [search, setSearch] = useState('');
  const [tags, setTags] = useState([]);
  const searchQuery = query.get('searchQuery');
  
  const searchPost = () => {
    if(search.trim() || tags){
      const tagsQuery = query.get('tags');
      const searchQuery = query.get('searchQuery');
      navigate(`/posts/search?searchQuery=${search || 'none'}&tags=${tags.join(',')}`);
      dispatch(getPostsBySearch({searchQuery, tags: tagsQuery}));
    } else {
      navigate('/');
    }
  }
  

  const handleKeyPress = (e) => {
    if(e.keyCode === 13) {
      searchPost();
    }
  } 

  const handleAdd = (tag) => {
    setTags([...tags, tag]);
  }
  const handleDelete = (deleteTag) => {
    setTags(tags.filter((tag) => tag !== deleteTag));
  }


  return (
    <Grow in>
        <Container maxWidth='xl'>
          <Grid className={classes.gridContainer} container justifyContent='space-between' alignItems='stretch' spacing={3}>
            <Grid item xs={12} sm={6} md={9}>
              <Posts setCurrentId={setCurrentId}/>
            </Grid>
            <Grid item xs={12} sm={6} md={3}>
              <AppBar className={classes.appBarSearch} position='static' color='inherit'>
                <TextField
                  name='search'
                  variant='outlined'
                  label='Search Memories'
                  fullWidth
                  value={search}
                  onKeyDown= {handleKeyPress}
                  onChange={(e)=> setSearch(e.target.value)}
                />
                <ChipInput
                  style={{margin: '10px 0'}}
                  value={tags}
                  onAdd={(chip) => handleAdd(chip)}
                  onDelete={(chip) => handleDelete(chip)}
                  label='Search Tags'
                  variant='outlined'
                />
                <Button onClick={searchPost} color='primary' variant='contained'>Search</Button>
              </AppBar>
              <Form currentId={currentId} setCurrentId={setCurrentId}/>
              {(!searchQuery && !tags.length) && (
                <Paper className={classes.pagination} elevation={6}>
                  <Paginate page={page}/>
                </Paper>
              )}
            </Grid>
          </Grid>
        </Container>
      </Grow>
  );
}

export default Home;
