import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import FileBase from 'react-file-base64'; 
import { useDispatch, useSelector } from 'react-redux';


import makeStyles from './styles.js';
import { createPost, updatePost } from '../../actions/postsActions';

const Form = ({currentId, setCurrentId}) => {
  const [postData, setPostData] = useState({ title:'', message:'', tags:'', baseImage:''});
  const editPost = useSelector((state) => currentId ? state.posts.find((post) => post._id === currentId) : null);
  const classes = makeStyles();
  const dispatch = useDispatch();
  const user = JSON.parse(localStorage.getItem('profile')).result;
  const userId = user?.sub 

  useEffect(() => {
    if(editPost) setPostData(editPost);
  }, [editPost]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if(currentId){
      dispatch(updatePost(currentId, postData));
    } else {
    dispatch(createPost({...postData, name: user.result.name, creator: user?.result?._id || user?.result?.sub}));
    }
    clear();
  }

  const clear = () => {
    setCurrentId(null);
    setPostData({ title:'', message:'', tags:'', baseImage:''});
  }

  if(!user?.result){
    return (
      <Paper className={classes.paper}>
        <Typography variant="h6" align='center'>
          Please Sign In to Create your own memories and like other's memories. 
        </Typography>
      </Paper>
    )
  }

  return (
    <Paper className={classes.paper}>
      <form autoComplete='off' noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant="h6">{currentId ? 'Editing' : 'Creating'} a Memory</Typography>
        <TextField name='title' variant='outlined' label="Title" fullWidth value={postData.title} onChange={(e) => setPostData({...postData, title: e.target.value})} />
        <TextField name='message' variant='outlined' label="Message" fullWidth value={postData.message} onChange={(e) => setPostData({...postData, message: e.target.value})} />
        <TextField name='tags' variant='outlined' label="Tags" fullWidth value={postData.tags} onChange={(e) => setPostData({...postData, tags: e.target.value.split(',')})} />
        <div className={classes.fileInput}>
          <FileBase type="file"
            multiple= {false}
            onDone= {({base64}) => setPostData({...postData, selectedFile: base64})}
          />
        </div>
        <Button className={classes.buttonSubmit} onClick={handleSubmit} variant="contained" color='primary' size='large' type='submit' fullWidth>Submit</Button>
        <Button variant="contained" color='secondary' size='small' onClick={clear} fullWidth>Clear</Button>
      </form>
    </Paper>
  );
}

export default Form;
