import { createReducer } from "@reduxjs/toolkit";

// initial state of posts
const initialPosts = [];

// action.pay
export default posts = createReducer(initialPosts,{
    FETCH_ALL: (state, action) =>{
        return state;
    },
    CREATE: (state, action) =>{
        return state;
    }
})
