import { CircularProgress, Divider, Paper, Typography } from '@material-ui/core';
import moment from 'moment';
import React, {useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import { getPostsBySearch, getPosts, getPost } from '../../actions/postsActions.js';
import useStyles from './styles.js';

const PostDetails = () => {
  const classes = useStyles();
  const {post, posts, isLoading} = useSelector((state) => state.posts)
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const {id} = useParams();

  const openPost = (id) => {
    navigate(`/posts/${id}`);
  }

  useEffect(() => {
    dispatch(getPost(id));
  }, [id]);
  
  useEffect(() => {
    if(post) {
      dispatch(getPostsBySearch({ search: 'none', tags: post?.tags.join(',') }));
      log
    }
  }, [post]);

  const recommendedPosts = posts.filter((current) => current._id !== post._id);

  if(!post) return null;

  if(isLoading) {
    return <Paper elevation={6} className={classes.loadingPaper}>
      <CircularProgress size='7em' />
    </Paper>
  }

  return (
    <Paper style={{padding: '20px', borderRadius: '15px'}} elevation={6}>
      <div className={classes.card}>
        <div className={classes.section}>
          <Typography variant="h3" component="h2">{post.title}</Typography>
          <Typography gutterBottom variant="h6" color="textSecondary" component="h2">{post.tags.map((tag) => `#${tag} `)}</Typography>
          <Typography gutterBottom variant="body1" component="p">{post.message}</Typography>
          <Typography variant="h6">Created by: {post.name}</Typography>
          <Typography variant="body1">{moment(post.createdAt).fromNow()}</Typography>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="body1"><strong>Realtime Chat - coming soon!</strong></Typography>
          <Divider style={{ margin: '20px 0' }} />
          <Typography variant="body1"><strong>Comments - coming soon!</strong></Typography>
          <Divider style={{ margin: '20px 0' }} />
        </div>
        <div className={classes.imageSection}>
          <img className={classes.media} src={post.selectedFile || 'https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png'} alt={post.title} />
        </div>
      </div>
      { recommendedPosts.length && (
        <div className={classes.section}>
          <Typography gutterBottom variant="h5">You might also like:</Typography>
          <Divider/>
          <div className={classes.recommendedPosts}>
            {recommendedPosts.map(({ title, message, name, likes, selectedFile, _id }) => (
              <div key={_id} style={{margin: '20px', cursor: 'pointer'}} onClick={()=> openPost(_id)}>
                { title }
              </div>
            ))}
          </div>
        </div>
      )}
    </Paper>
  );
}

export default PostDetails;
